mapper.select:
SELECT b.seq , b.codeGroup_seq , a.propertyKor , b.codeseq , b.anothercode , b.name , b.nameEng , b.useNY , b.regDate , b.modDate FROM CodeGroup a INNER JOIN Code b on a.seq = b.CodeGroup_seq
AND a.delNY = #{shDelNY}
AND a.num = #{shValue } AND a.propertyKor LIKE CONCAT('%',#{shValue },'%') AND property LIKE CONCAT('%',#{shValue },'%')

mapper.insert:
INSERT INTO Code (
	codeGroup_seq
	, codeseq
	, name
	, nameEng
	, useNY
)
VALUES (
	#{codeGroup_seq}
	,#{codeseq}
	,#{name}
	,#{nameEng}
	,1
)


	SELECT last_insert_id()
