mapper.select:
SELECT b.seq , b.codeGroup_seq , a.propertyKor , b.codeseq , b.anothercode , b.name , b.nameEng , b.useNY , b.regDate , b.modDate FROM CodeGroup a INNER JOIN Code b on a.seq = b.CodeGroup_seq WHERE 1=1
AND b.useNY = #{shUseNY}
AND b.codeGroup_seq = #{shValue } AND b.name LIKE CONCAT('%',#{shValue },'%') AND b.nameEng LIKE CONCAT('%',#{shValue },'%') AND a.propertyKor LIKE CONCAT('%',#{shValue },'%')

mapper.insert:
INSERT INTO Code (
	codeGroup_seq
	, codeseq
	, name
	, nameEng
	, useNY
)
VALUES (
	#{codeGroup_seq}
	,#{codeseq}
	,#{name}
	,#{nameEng}
	,1
)


	SELECT last_insert_id()
