mapper.sql:
FROM CodeGroup a
INNER JOIN Code b on a.seq = b.CodeGroup_seq
WHERE 1=1

AND b.useNY = #{shUseNY}


	AND b.codeGroup_seq = #{shValue }
	AND b.name LIKE CONCAT('%',#{shValue },'%')
	AND b.nameEng LIKE CONCAT('%',#{shValue },'%')
	AND a.propertyKor LIKE CONCAT('%',#{shValue },'%')

	AND b.regDate BETWEEN #{shDateStart} AND #{shDateEnd}

	AND b.modDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO Code (
	codeGroup_seq
	,codeseq
	,name
	,nameEng
	,useNY
	,regDate
	,modDate
)
VALUES (
	#{codeGroup_seq}
	,#{codeseq}
	,#{name}
	,#{nameEng}
	,1
	,now()
	,now()
)


	SELECT last_insert_id()

mapper.update:
UPDATE Code 
SET
	codeseq = #{codeseq}
	, codeGroup_seq = #{codeGroup_seq}
	, name = #{name}
	, nameEng = #{nameEng}
	, modDate = now()
WHERE
	seq = #{seq}
