mapper.select:
SELECT a.seq , a.num , a.propertyKor , property , (SELECT COUNT(b.codeGroup_seq) FROM Code b WHERE a.num = b.codeGroup_seq) as codeNum , a.regDate , a.modDate , a.useNY , a.delNY From CodeGroup a WHERE 1=1
AND a.delNY = #{shDelNY}
AND a.num = #{shValue } AND a.propertyKor LIKE CONCAT('%',#{shValue },'%') AND property LIKE CONCAT('%',#{shValue },'%') AND a.regDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.modDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO CodeGroup (
	num
	,propertyKor
	,property
	,regDate
	,modDate
	,useNY
	,delNY
)
VALUES (
	#{num}
	,#{propertyKor}
	,#{property}
	,#{regDate}
	,#{modDate}
	,1
	,0
)


	SELECT last_insert_id()

mapper.update:
UPDATE CodeGroup
SET 
	propertyKor = #{propertyKor}
	, property = #{property}
	, modDate = now()
WHERE 
 	seq = #{seq}
