mapper.sql:
FROM item a INNER JOIN brand_list b on a.brand_list_seq = b.num WHERE 1=1
AND a.stock = #{shStock}
AND bname LIKE CONCAT('%',#{shValue },'%') AND a.name LIKE CONCAT('%',#{shValue },'%') AND numPurchase LIKE CONCAT('%',#{shValue },'%')
AND a.regist BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO item (
	name
	,discount
	,price
	,info_item
	,regist
	,stock
	,useNY
	,delNY
	,brand_list_seq
)
VALUES (
	#{name}
	,#{discount}
	,#{price}
	,#{info_item}
	,now()
	,1
	,1
	,0
	,#{brand_list_seq}
)


	SELECT last_insert_id()

mapper.update:
UPDATE item
SET 
	name = #{name}
	,price = #{price}
	,info_item = #{info_item}
	,brand_list_seq = #{brand_list_seq}
WHERE 
	itemSeq = #{itemSeq}
