mapper.sql:
FROM item a INNER JOIN brand_list b on a.brand_list_seq = b.num WHERE 1=1
AND a.stock = #{shStock}
AND bname LIKE CONCAT('%',#{shValue },'%') AND a.name LIKE CONCAT('%',#{shValue },'%') AND numPurchase LIKE CONCAT('%',#{shValue },'%')
AND a.regist BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO Item (
	name
	,discount
	,price
	,info_item
	,info_purchase
	,regist
	,stock
	,useNY
	,delNY
	,brand_list_seq
)
VALUES (
	#{name}
	,#{discount}
	,#{price}
	,#{info_item}
	,#{info_purchase}
	,now()
	,1
	,1
	,0
	,#{brand_list_seq}
)


	SELECT last_insert_id()
