mapper.sql:
FROM user a
WHERE 1=1

	AND a.gender = #{shGender}
	AND a.userDelNY = #{shUserDelNY}


		AND a.seq = #{shValue }
		AND a.userGrade LIKE CONCAT('%',#{shValue },'%')
		AND a.name LIKE CONCAT('%',#{shValue },'%')
		AND a.email LIKE CONCAT('%',#{shValue },'%')



		AND a.accessDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO user (
	name
	,dob
	,tel
	,telMid
	,telEnd
	,userID
	,pw
	,nickname
	,zip
	,addr1
	,addr2
	,addr3
	,emailID
	,emailseq
	,email
	,userDelNY
	,accessDate
	,userGrade
	,gender
)
VALUES (
	#{name}
	,#{dob}
	,#{tel}
	,#{telMid}
	,#{telEnd}
	,#{userID}
	,#{pw}
	,#{nickname}
	,#{zip}
	,#{addr1}
	,#{addr2}
	,#{addr3}
	,#{emailID}
	,#{emailseq}
	,#{email}
	,0
	,now()
	,"베이비올리브"
	,#{gender}
)


	SELECT last_insert_id()

mapper.update:
	UPDATE user
	SET 
		dob = #{dob}
		, tel = #{tel}
		, userID = #{userID}
		, pw = #{pw}
		, nickname = #{nickname}
		, email = CONCAT(#{emailID},

				'@naver.com')
				'@gmail.com')
				'@hanmail.com')
				'@nate.com')
				'@daum.net')
				'@kakao.com')

		, emailID = #{emailID}	
		, emailseq = #{emailseq}	
		, telMid = #{telMid}
		, telEnd = #{telEnd}
		, zip = #{zip}   
		, addr1 = #{addr1}
		, addr2 = #{addr2}
		, addr3 = #{addr3}
		, accessDate = now()
		, gender = #{gender}
		, userGrade = CONCAT(#{userGrade},

				'BABY OLIVE')
				'PINK OLIVE')
				'GREEN OLIVE')
				'BLACK OLIVE')
				'GOLD OLIVE')
				'관리자')

	WHERE 
 		seq = #{seq}

mapper.select:
SELECT seq , userID , userGrade FROM user WHERE 1=1 AND userID = #{userID}
AND pw = #{pw}
AND userDelNY = 0

mapper.update:
