mapper.sql:
FROM user a
WHERE 1=1

	AND a.gender = #{shGender}
	AND a.userDelNY = #{shUserDelNY}


		AND a.seq = #{shValue }
		AND a.userGrade LIKE CONCAT('%',#{shValue },'%')
		AND a.name LIKE CONCAT('%',#{shValue },'%')
		AND a.email LIKE CONCAT('%',#{shValue },'%')



		AND a.accessDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO user (
	name
	,gender
	,dob
	,tel
	,telMid
	,telEnd
	,userID
	,pw
	,nickname
	,email
	,userDelNY
	,accessDate
	,userGrade
)
VALUES (
	#{name}
	,#{gender}
	,#{dob}
	,#{tel}
	,#{telMid}
	,#{telEnd}
	,#{userID}
	,#{pw}
	,#{nickname}
	,#{email}
	,0
	,now()
	,#{userGrade}
)


	SELECT last_insert_id()

mapper.update:
	UPDATE user
	SET 
		pw = #{pw}
		, nickname = #{nickname}
		, userDelNY = #{userDelNY}
	WHERE 
 		seq = #{seq}
